  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "  \uCE21\uC815\uB41C \uAC12\uC740 \uBCC0\uD654\uC758 \uC2DC\uC791\uC774\uBA74\uC11C, \uBCC0\uD654\uB97C \uCD94\uC801\uD558\uACE0, \uC5ED\uC0AC\uC5D0\uB3C4 \uC2E4\uB85D\uCC98\uB7FC \uB370\uC774\uD130\uB85C \uB0A8\uB294\uB2E4. \uADF8\uB798\uC11C \uC77C\uBD80 \uC815\uCE58\uC778\uACFC \uC815\uAD8C, \uACBD\uC601\uC790\uB4E4\uC740 \uD1B5\uACC4\uB97C \uC870\uC791\uD558\uACE0 \uC2F6\uC740 \uC695\uB9DD\uC5D0 \uBE60\uC9C0\uAE30\uB3C4\uD55C\uB2E4. \uC870\uC120\uB9CC\uD07C \uC601\uC18D\uC801\uC778 \uD68C\uC0AC\uB97C \uB9CC\uB4E4\uACE0 \uC2F6\uC9C0 \uC54A\uC740\uAC00? \uC870\uC120\uC774 600\uB144\uC744 \uB118\uAC8C \uC9C0\uC18D\uD560 \uC218 \uC788\uB358 \uBC30\uACBD\uC5D4 \uC5EC\uB7EC\uAC00\uC9C0 \uC6D0\uC778\uC774 \uC788\uACA0\uC9C0\uB9CC, \uC2E4\uB85D\uC73C\uB85C \uC655\uB4E4\uC758 \uC77C\uAC70\uC218\uC77C\uD22C\uC871\uC774 \uB0A8\uB294 \uBB34\uC5B8\uC758 \uACAC\uC81C\uAC00 \uC788\uC5C8\uACE0, \u2018\uACBD\uC81C\uC721\uC804\u2019,\u2018\uACBD\uAD6D\uB300\uC804\u2019 \uB4F1 \uBC95\uC801 \uADFC\uAC70\uB85C \uAD6D\uAC00\uAC00 \uC6B4\uC601\uB418\uB3C4\uB85D \uD588\uAE30 \uB54C\uBB38\uC774\uB2E4. \uACBD\uC601\uD559\uC758 \uAD6C\uB8E8 \uD53C\uD130 \uB4DC\uB7EC\uCEE4\uB294 \u201C\uCE21\uC815\uD560 \uC218 \uC5C6\uC73C\uBA74 \uAD00\uB9AC\uD560 \uC218 \uC5C6\uACE0, \uAD00\uB9AC\uD560 \uC218 \uC5C6\uC73C\uBA74 \uAC1C\uC120\uD560 \uC218 \uC5C6\uB2E4\u201D\uB77C\uB294 \uBA85\uC5B8\uC744 \uB0A8\uACBC\uB2E4. "), mdx("p", null, "  AB Test\uB9CC\uD07C \uC11C\uBE44\uC2A4\uC758 \uBCC0\uD654\uB97C \uCE21\uC815\uD558\uACE0, \uADF8 \uACB0\uACFC\uB97C \uBD84\uC11D\uD558\uBA74\uC11C \uAE30\uB85D\uC5D0 \uB0A8\uAE30\uBA74\uC11C, \uD6C4\uC784\uC790\uB4E4\uC774 \uB354 \uB098\uC544\uAC08 \uC218 \uC788\uB3C4\uB85D \uB2E4\uC591\uD55C \uC0AC\uB840\uB4E4\uACFC \uC778\uC0AC\uC774\uD2B8\uB97C \uB0A8\uAE30\uB294 \uAC83 \uCC98\uB7FC \uD6CC\uB96D\uD558\uACE0 \uAC74\uC804\uD55C \uD504\uB85C\uC138\uC2A4\uB3C4 \uC5C6\uB2E4. \uC2EC\uC9C0\uC5B4 \uC5B4\uB5A4 \uACB0\uACFC\uB97C \uC120\uD0DD\uD560\uC9C0 \uB370\uC774\uD130\uB97C \uC911\uC2EC\uC5D0 \uB450\uACE0, \uC815\uCC45\uACFC \uC804\uB7B5\uC744 \uACE0\uB824\uD574\uC11C \uC120\uD0DD\uD558\uAC8C\uB41C\uB2E4. \uB370\uC774\uD130\uAC00 \uC911\uC2EC\uC774 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC9C1\uAE09\uC774 \uB192\uACE0 \uB0AE\uACE0, \uB3C4\uBA54\uC778 \uC804\uBB38\uC131\uC774 \uC788\uACE0 \uC5C6\uACE0\uB97C \uB5A0\uB098, \uC2E4\uD5D8 \uD658\uACBD\uC5D0\uC11C \uC720\uC800\uB4E4\uC774 \uACBD\uD5D8\uD55C \uACB0\uACFC\uB97C \uD1A0\uB300\uB85C\uB9CC \uD1A0\uB860\uD558\uACE0 \uACB0\uC815\uD560 \uC218 \uC788\uB2E4. "), mdx("p", null, "  \uD558\uC9C0\uB9CC \uBAA8\uB4E0 \uB3C4\uAD6C\uB294 \uC0AC\uB78C\uB4E4\uC774 \uC5B4\uB5BB\uAC8C \uC4F0\uB294\uC9C0\uC5D0 \uB530\uB77C \uC0AC\uB78C\uC744 \uC0B4\uB9AC\uB294 \uCE7C\uC774 \uB418\uAE30\uB3C4 \uD558\uACE0, \uC0B4\uC778 \uB3C4\uAD6C\uAC00 \uB418\uAE30\uB3C4\uD55C\uB2E4. \uD575\uAE30\uC220\uB3C4 \uC801\uC740 \uC790\uC6D0\uC73C\uB85C \uD070 \uC5F4\uC5D0\uB108\uC9C0\uB97C \uC5BB\uB294 \uC790\uC6D0\uC774\uB418\uAE30\uB3C4 \uD558\uC9C0\uB9CC, \uC218\uC2ED \uC218\uBC31\uB9CC\uC758 \uC0AC\uB78C\uC744 \uD55C\uBC88\uC5D0 \uC8FD\uC77C \uC218 \uC788\uB294 \uBB34\uAE30\uAC00 \uB418\uB294 \uAC83 \uCC98\uB7FC. \uC9C0\uAE08 \uBD80\uD130\uB294 \uC628\uB77C\uC778 \uC2E4\uD5D8, AB Test\uB77C\uB294 \uC88B\uC740 \uB3C4\uAD6C\uB97C \uC11C\uBE44\uC2A4\uC640 \uAE30\uC5C5\uC744 \uC0B4\uB9AC\uB294 \uB3C4\uAD6C\uAC00 \uC544\uB2C8\uB77C, \uD2B9\uC815 \uAC1C\uC778\uC758 \uC601\uB2EC\uACFC \uC790\uC544\uC2E4\uD604\uC744 \uC704\uD574 \uC545\uC6A9\uD558\uC5EC \uC870\uC9C1\uC758 \uB9AC\uC18C\uC2A4\uB97C \uB0AD\uBE44\uD558\uACE0, \uBCC0\uD654\uB97C \uC678\uBA74\uD558\uC5EC \uD1F4\uD589\uD558\uB294 \uC720\uD615\uB4E4\uC744 \uC124\uBA85\uD558\uB824\uACE0\uD55C\uB2E4. \uBB3C\uB860 \uACBD\uD5D8\uB2F4\uC740 \uC544\uB2C8\uACE0 \uB0B4 \uCE5C\uAD6C\uC758 \uC774\uC57C\uAE30\uC774\uB2E4. \uD604\uC7AC \uB2E4\uB2C8\uACE0 \uC788\uB294 \uD68C\uC0AC\uC758 \uC774\uC57C\uAE30\uB294 \uC808\uB300 \uC544\uB2C8\uB2C8 \uC624\uD574\uB294 \uB9C8\uC2DC\uB77C. ABT\uB77C\uB294 \uC88B\uC740 \uB3C4\uAD6C\uB97C \uC5BB\uC5C8\uC9C0\uB9CC, \uC774\uB97C \uB0B4\uAC00 \uC545\uC6A9\uD558\uACE0 \uC788\uC9C0\uB294 \uC544\uB2CC\uC9C0 \uB3CC\uC544\uBCF4\uB294\uB370 \uB3C4\uC6C0\uC774 \uB418\uC168\uC73C\uBA74 \uD55C\uB2E4. "), mdx("h3", {
    "id": "1-ab-test-결과보다-본인의-직관이-뛰어나다고-과신하며-기대와-다른-결과가-나오면-원하는-결과가-나올때까지-실험을-반복하거나-실험-조건을-조작한다"
  }, "1. AB Test \uACB0\uACFC\uBCF4\uB2E4 \uBCF8\uC778\uC758 \uC9C1\uAD00\uC774 \uB6F0\uC5B4\uB098\uB2E4\uACE0 \uACFC\uC2E0\uD558\uBA70, \uAE30\uB300\uC640 \uB2E4\uB978 \uACB0\uACFC\uAC00 \uB098\uC624\uBA74 \uC6D0\uD558\uB294 \uACB0\uACFC\uAC00 \uB098\uC62C\uB54C\uAE4C\uC9C0 \uC2E4\uD5D8\uC744 \uBC18\uBCF5\uD558\uAC70\uB098, \uC2E4\uD5D8 \uC870\uAC74\uC744 \uC870\uC791\uD55C\uB2E4."), mdx("p", null, "  \uBCF4\uD1B5 \uC2A4\uD2F0\uBE0C\uC7A1\uC2A4\uBCD1\uC5D0 \uAC78\uB838\uAC70\uB098 \uB2A5\uB825\uC5D0 \uBE44\uD574 \uAC00\uBC29\uB048\uC774 \uAE38\uC5B4\uC11C \uC790\uAE30 \uD655\uC2E0\uC758 \uD568\uC815\uC5D0 \uBE60\uC9C4 \uBD84\uB4E4\uC5D0\uC11C \uC790\uC8FC \uB098\uD0C0\uB098\uB294 \uACBD\uC6B0\uB2E4. \uB610\uB294 ABT\uB97C \uC2E4\uD5D8\uC774 \uC544\uB2CC \uCD9C\uC2DC\uB97C \uC704\uD55C \uD1B5\uACFC \uC758\uB840\uB85C\uB9CC \uC0DD\uAC01\uD558\uB294 \uC0AC\uB78C\uB4E4\uC5D0\uAC8C\uC11C\uB3C4 \uBC1C\uC0DD\uD55C\uB2E4. \uD544\uC790\uC640 \uD544\uC790\uC640 \uD568\uAED8 \uC77C\uD558\uB294 \uB3D9\uB8CC\uB4E4\uC740 \uACB8\uC190\uD574\uC9C8 \uB9CC\uD07C \uC2E4\uD5D8\uACFC\uC815\uC5D0\uC11C \uAC01\uC790\uC758 \uB300\uC548\uB4E4\uC774 \uBB34\uCC38\uD558\uAC8C \uC2E4\uD328\uD558\uB294 \uACBD\uD5D8\uC744 \uD574\uBCF4\uC558\uB2E4. \uAD6C\uC870\uC0C1\uC73C\uB85C \uB2F9\uC5F0\uD788 \uD074\uB9AD\uACFC \uB9E4\uCD9C\uC774 \uC624\uB97C \uC218 \uBC16\uC5D0 \uC5C6\uB2E4\uB294 \uC2A4\uD399\uB3C4, \uD074\uB9AD\uC774 \uB35C \uBC1C\uC0DD\uD558\uB294 \uACBD\uC6B0\uB3C4 \uC788\uC5C8\uACE0, \uACC1\uB2E4\uB9AC\uB85C \uB123\uC5B4\uBCF8 Treatment\uAC00 \uCC44\uD0DD\uB41C \uACBD\uD5D8\uB3C4 \uC801\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC774\uB2E4. \uB9C8\uCE58 \uCE5C\uAD6C\uB530\uB77C \uC624\uB514\uC158 \uC640\uC11C \uD569\uACA9\uD55C \uACBD\uC6B0\uB784\uAE4C. \uADF8\uB798\uC11C \uC77C\uBC18\uC801\uC778 \uC2E4\uD5D8\uC790\uB294 \uD56D\uC0C1 \uACB8\uC190\uD558\uB2E4. \uB2F4\uB2F9\uC790\uAC00 \uC120\uD638\uD558\uB294 \uC548\uC774 \uCC44\uD0DD\uB418\uBA74 \uB300\uC911\uC758 \uB208\uB192\uC774\uC640 \uB9DE\uC544\uAC04\uB2E4\uB294 \uC7AC\uBBF8\uAC00 \uC788\uC9C0, \uBCF8\uC778\uC774 \uB611\uB611\uD558\uB2E4\uACE0 \uC790\uB9CC\uD558\uB294\uB370\uAE4C\uC9C0 \uC774\uC5B4\uC9C0\uC9C0 \uC54A\uB294\uB2E4. \uC2E4\uD5D8\uACFC\uC815\uC5D0\uC11C\uB294 \uBB34\uC218\uD55C \uC2E4\uD328\uB97C \uACAA\uAE30 \uB54C\uBB38\uC774\uB2E4. \uD558\uC9C0\uB9CC \uC628\uB77C\uC778 \uD14C\uC2A4\uD2B8\uB97C \uCDA9\uBD84\uD788 \uD574\uBCF8 \uACBD\uD5D8\uC774 \uC5C6\uAC70\uB098, FGD\uAC00 \uB9C8\uCE58 \uB300\uC911\uACFC \uBAA8\uB4E0 \uC720\uC800\uB97C \uB300\uD45C\uD55C\uB2E4\uB294 \uCC29\uAC01\uC744\uD558\uACE0 FGD\uACB0\uACFC\uB85C\uB9CC \uD310\uB2E8\uD574\uC628 \uBD84\uB4E4\uC740 \uB098\uB984\uC758 \uD544\uD130\uBC84\uBE14\uC5D0 \uC758\uD574 \uC77C\uBC18 \uC0AC\uC6A9\uC790\uC640 \uC774\uACA9\uC774 \uC788\uB294 \uAC10\uAC01\uC744 \uAC00\uC9C0\uB294 \uACBD\uC6B0\uAC00 \uB9CE\uB2E4. \uD2B9\uD788\uB098 \uB300\uC911\uB4E4\uC5D0\uAC8C \uC790\uC2E0\uC758 \uACB0\uACFC\uBB3C\uC744 \uAC80\uC99D\uBC1B\uB294\uAC8C \uC544\uB2CC \uC18C\uC218\uC758 \uC784\uC6D0\uB4E4\uACFC \uD559\uC790\uB4E4\uC5D0\uAC8C\uB9CC \uD3C9\uAC00\uB97C \uBC1B\uC544\uC628 \uBD84\uB4E4\uC77C \uC218\uB85D \uC5D8\uB9AC\uD2B8 \uD544\uD130\uBC84\uBE14\uC5D0\uC11C \uB098\uC624\uC9C0 \uBABB\uD558\uACE0, ABT\uACB0\uACFC\uB97C \uC218\uC6A9\uD558\uC9C0 \uBABB\uD574, \uC0D8\uD50C\uB9C1\uC744 \uC65C\uACE1\uD558\uAC70\uB098 \uD2B9\uC815 \uC9C0\uD45C\uB9CC\uC73C\uB85C \uACB0\uACFC\uB97C \uD574\uC11D\uD558\uB294 \uB4F1 \uC544\uC804\uC778\uC218\uC801\uC778 \uD310\uB2E8\uC744 \uD558\uB294 \uACBD\uC6B0\uB97C \uC801\uC9C0 \uC54A\uAC8C \uBCF4\uC558\uB2E4."), mdx("p", null, "  \uC628\uB77C\uC778\uD14C\uC2A4\uD2B8\uC758 60% \uC774\uC0C1\uC740 \uCC44\uD0DD\uD558\uC9C0 \uC54A\uACE0 \uBC84\uB9B0\uB2E4\uB294 \uB9D0\uC774 \uC788\uB2E4. \uC2E4\uC81C\uB85C \uD544\uC790\uC758 \uACBD\uC6B0\uC5D0\uB3C4 \uBC84\uD0B7\uC744 \uB9CC\uB4E4\uBA74 \uC2E4\uC81C \uC0AC\uC6A9\uB418\uB294 \uBE44\uC728\uC740 30%\uAC00 \uC548\uB41C\uB2E4. \uC2EC\uC9C0\uC5B4 \uC2E4\uD5D8\uC744 \uAD6C